class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comment/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(o),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n    <div id="user-comment">\n      <div class="user-comment-content">\n        <p>\n          ${e.content}\n        </p>\n        <div id="comment-user-name">\n          <p>\n            Comment by :\n            <a href="/user/profile/${e.user._id}">\n            ${e.user.name}\n            </a>\n          </p>\n        </div>\n      </div>\n      <div>\n        <a\n          class="toggle-like-button"\n          data-likes="${e.likes.length}"\n          href="/likes/toggle/?id=${e._id}&type=Comment"\n        >\n          ${e.likes.length}Likes\n        </a>\n        <a class="delete-comment-button" href="/comment/delete/${e._id}">DELETE</a>\n      </div>\n    </div>\n  </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}